<script lang="ts" setup>
import { ref } from 'vue'

type FormFeedbackType = 'incomplete' | 'agree' | 'email' | 'phone' | 'success' | null

/* -----START V-MODELS----- */
const isLoading = ref(false),
      success   = ref(true),
      email     = ref('')
/* -----END Agree----- */

/* -----START Feedback----- */
const formFeedback: Ref<FormFeedbackType> = ref(null),
      textFeedback = {
        error: 'There was an error processing your request.',
        success: 'Thanks for signing up!',
        incomplete: 'Please complete all required fields.',
        email: 'Please enter a valid email address.',
        phone: 'Please enter a valid phone number.',
        agree: 'Please agree to terms and conditions.'
      }

const setFeedback = (type, status) => {
  formFeedback.value = type
  isLoading.value = false
  success.value = status
  setTimeout(() => {
    formFeedback.value = null
  }, 2000)
}
/* -----START Feedback----- */

/* -----START SUBMIT----- */
const submitForm = async () => {
  isLoading.value = true
  formFeedback.value = null

  if( !email.value.trim() ) {
    setFeedback('incomplete', null)
    return
  }

  const regex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i
  if (email.value && !regex.test(email.value)) {
    setFeedback('email', false)
    return
  }
  console.log('save email to db')
  setFeedback('success', true)
}
/* -----END SUBMIT----- */
</script>

